package com.google.crypto.tink.signature;


import HashType.SHA1;
import HashType.SHA256;
import com.google.crypto.tink.PublicKeyVerify;
import com.google.crypto.tink.TestUtil;
import com.google.crypto.tink.proto.HashType;
import com.google.crypto.tink.proto.RsaSsaPkcs1PublicKey;
import com.google.crypto.tink.subtle.Hex;
import java.security.GeneralSecurityException;
import org.junit.Assert;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.junit.runners.JUnit4;


/**
 * Unit tests for RsaSsaPkcs1VerifyKeyManager.
 */
@RunWith(JUnit4.class)
public final class RsaSsaPkcs1VerifyKeyManagerTest {
    static class NistTestVector {
        byte[] modulus;

        byte[] exponent;

        byte[] msg;

        byte[] sig;

        HashType hashType;

        public NistTestVector(String modulus, String exponent, String msg, String sig, HashType hashType) {
            this.modulus = TestUtil.hexDecode(modulus);
            this.exponent = TestUtil.hexDecode(exponent);
            this.msg = TestUtil.hexDecode(msg);
            this.sig = TestUtil.hexDecode(sig);
            this.hashType = hashType;
        }
    }

    // Test vector from
    // https://csrc.nist.gov/Projects/Cryptographic-Algorithm-Validation-Program/Digital-Signatures
    final RsaSsaPkcs1VerifyKeyManagerTest.NistTestVector[] nistTestVectors = new RsaSsaPkcs1VerifyKeyManagerTest.NistTestVector[]{ new RsaSsaPkcs1VerifyKeyManagerTest.NistTestVector("", "49d2a1", "95123c8d1b236540b86976a11cea31f8bd4e6c54c235147d20ce722b03a6ad756fbd918c27df8ea9ce3104444c0bbe877305bc02e35535a02a58dcda306e632ad30b3dc3ce0ba97fdf46ec192965dd9cd7f4a71b02b8cba3d442646eeec4af590824ca98d74fbca934d0b6867aa1991f3040b707e806de6e66b5934f05509bea", "", HashType.SHA256), new RsaSsaPkcs1VerifyKeyManagerTest.NistTestVector("", "fa3751", "6459ea1d443df706907ffdd3ca2f193f93f5a349b50357d26748b767cde6ab5cbfe76b1acb2b9eb97da5c4d2ddc8d18e3a3b1a0326d475c1c2c49ca73c0fd3fc9540cbbba85ac52d6811fabd693a3b09a281d535715ab784df3ad7292606d15a70ccd1a7e2b1b48ad92a6a3f736f9fd5522d9a869c7b654446102e9493b3ed9f", "", HashType.SHA512) };

    @Test
    public void testNistTestVector() throws Exception {
        if (TestUtil.isTsan()) {
            // This test times out when running under thread sanitizer, so we just skip.
            return;
        }
        for (RsaSsaPkcs1VerifyKeyManagerTest.NistTestVector t : nistTestVectors) {
            RsaSsaPkcs1PublicKey pubKey = TestUtil.createRsaSsaPkcs1PubKey(t.modulus, t.exponent, t.hashType);
            RsaSsaPkcs1VerifyKeyManager keyManager = new RsaSsaPkcs1VerifyKeyManager();
            PublicKeyVerify verifier = keyManager.getPrimitive(pubKey);
            try {
                verifier.verify(t.sig, t.msg);
            } catch (GeneralSecurityException e) {
                Assert.fail(("Valid signature, should not throw exception" + e));
            }
            for (TestUtil.BytesMutation mutation : TestUtil.generateMutations(t.sig)) {
                try {
                    verifier.verify(mutation.value, t.msg);
                    Assert.fail(String.format(("Invalid signature, should have thrown exception : sig = %s, msg = %s," + " description = %s"), Hex.encode(mutation.value), Hex.encode(t.msg), mutation.description));
                } catch (GeneralSecurityException expected) {
                    // Expected.
                }
            }
        }
    }

    @Test
    public void testSmallModulus() throws Exception {
        try {
            RsaSsaPkcs1PublicKey pubKey = TestUtil.createRsaSsaPkcs1PubKey(TestUtil.hexDecode("23"), TestUtil.hexDecode("03"), SHA256);
            RsaSsaPkcs1VerifyKeyManager keyManager = new RsaSsaPkcs1VerifyKeyManager();
            keyManager.getPrimitive(pubKey);
            Assert.fail("Invalid modulus, should have thrown exception");
        } catch (GeneralSecurityException e) {
            TestUtil.assertExceptionContains(e, "2048-bit");
        }
    }

    @Test
    public void testInvalidHash() throws Exception {
        RsaSsaPkcs1VerifyKeyManagerTest.NistTestVector t = nistTestVectors[0];
        try {
            RsaSsaPkcs1PublicKey pubKey = TestUtil.createRsaSsaPkcs1PubKey(t.modulus, t.exponent, SHA1);
            RsaSsaPkcs1VerifyKeyManager keyManager = new RsaSsaPkcs1VerifyKeyManager();
            keyManager.getPrimitive(pubKey);
            Assert.fail("Invalid hash, should have thrown exception");
        } catch (GeneralSecurityException e) {
            TestUtil.assertExceptionContains(e, "SHA1");
        }
    }
}

