package com.google.crypto.tink.signature;


import HashType.SHA1;
import HashType.SHA256;
import com.google.crypto.tink.PublicKeyVerify;
import com.google.crypto.tink.TestUtil;
import com.google.crypto.tink.proto.HashType;
import com.google.crypto.tink.proto.RsaSsaPssPublicKey;
import java.security.GeneralSecurityException;
import org.junit.Assert;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.junit.runners.JUnit4;


/**
 * Unit tests for RsaSsaPssVerifyKeyManager.
 */
@RunWith(JUnit4.class)
public final class RsaSsaPssVerifyKeyManagerTest {
    static class NistTestVector {
        byte[] modulus;

        byte[] exponent;

        byte[] msg;

        byte[] sig;

        HashType sigHash;

        HashType mgf1Hash;

        int saltLength;

        public NistTestVector(String modulus, String exponent, String msg, String sig, HashType sigHash, HashType mgf1Hash, int saltLength) {
            this.modulus = TestUtil.hexDecode(modulus);
            this.exponent = TestUtil.hexDecode(exponent);
            this.msg = TestUtil.hexDecode(msg);
            this.sig = TestUtil.hexDecode(sig);
            this.sigHash = sigHash;
            this.mgf1Hash = mgf1Hash;
            this.saltLength = saltLength;
        }
    }

    // Test vector from
    // https://csrc.nist.gov/Projects/Cryptographic-Algorithm-Validation-Program/Digital-Signatures
    final RsaSsaPssVerifyKeyManagerTest.NistTestVector[] nistTestVectors = new RsaSsaPssVerifyKeyManagerTest.NistTestVector[]{ new RsaSsaPssVerifyKeyManagerTest.NistTestVector("", "", "e002377affb04f0fe4598de9d92d31d6c786040d5776976556a2cfc55e54a1dcb3cb1b126bd6a4bed2a184990ccea773fcc79d246553e6c64f686d21ad4152673cafec22aeb40f6a084e8a5b4991f4c64cf8a927effd0fd775e71e8329e41fdd4457b3911173187b4f09a817d79ea2397fc12dfe3d9c9a0290c8ead31b6690a6", "", HashType.SHA256, HashType.SHA256, 32), new RsaSsaPssVerifyKeyManagerTest.NistTestVector("", "", "cc21593a6a0f737e2970b7c07984b070d761726296a07e24e056e68ff846b29cc1548179843d74dcee86479858b2c16e4cb84f2544b4ecdcb4dd43a04bb7183a768ae44a2712bf9ad47883acc2812f958306890ebea408c92eb4f001ed7dbf55f3a9c8d6d9f61e5fe32eb3253e59c18e863169478cd69b9155c335db66016f96", "", HashType.SHA512, HashType.SHA512, 0) };

    @Test
    public void testNistTestVector() throws Exception {
        for (RsaSsaPssVerifyKeyManagerTest.NistTestVector t : nistTestVectors) {
            RsaSsaPssPublicKey pubKey = TestUtil.createRsaSsaPssPubKey(t.modulus, t.exponent, t.sigHash, t.mgf1Hash, t.saltLength);
            RsaSsaPssVerifyKeyManager keyManager = new RsaSsaPssVerifyKeyManager();
            PublicKeyVerify verifier = keyManager.getPrimitive(pubKey);
            try {
                verifier.verify(t.sig, t.msg);
            } catch (GeneralSecurityException e) {
                Assert.fail(("Valid signature, should not throw exception" + e));
            }
        }
    }

    @Test
    public void testSmallModulus() throws Exception {
        try {
            RsaSsaPssPublicKey pubKey = TestUtil.createRsaSsaPssPubKey(TestUtil.hexDecode("23"), TestUtil.hexDecode("03"), SHA256, SHA256, 32);
            RsaSsaPssVerifyKeyManager keyManager = new RsaSsaPssVerifyKeyManager();
            keyManager.getPrimitive(pubKey);
            Assert.fail("Invalid modulus, should have thrown exception");
        } catch (GeneralSecurityException e) {
            TestUtil.assertExceptionContains(e, "2048-bit");
        }
    }

    @Test
    public void testInvalidHash() throws Exception {
        RsaSsaPssVerifyKeyManagerTest.NistTestVector t = nistTestVectors[0];
        try {
            RsaSsaPssPublicKey pubKey = TestUtil.createRsaSsaPssPubKey(t.modulus, t.exponent, SHA1, SHA256, 20);
            RsaSsaPssVerifyKeyManager keyManager = new RsaSsaPssVerifyKeyManager();
            keyManager.getPrimitive(pubKey);
            Assert.fail("Invalid hash, should have thrown exception");
        } catch (GeneralSecurityException e) {
            TestUtil.assertExceptionContains(e, "SHA1");
        }
    }
}

