package org.web3j.protocol.core;


import org.hamcrest.CoreMatchers;
import org.junit.Assert;
import org.junit.Test;
import org.web3j.protocol.ResponseTester;
import org.web3j.protocol.core.methods.response.EthBlock;
import org.web3j.protocol.core.methods.response.Web3ClientVersion;


/**
 * Raw Response tests.
 */
public class RawResponseTest extends ResponseTester {
    private static final String RAW_RESPONSE = "{\n" + ((("  \"id\":67,\n" + "  \"jsonrpc\":\"2.0\",\n") + "  \"result\": \"Mist/v0.9.3/darwin/go1.4.1\"\n") + "}");

    // CHECKSTYLE:OFF
    private static final String LARGE_RAW_RESPONSE = "{\"jsonrpc\":\"2.0\",\"id\":1,\"result\":{\"difficulty\":\"0x1aea99\",\"extraData\":\"0xd5830104028650617269747986312e31322e31826c69\",\"gasLimit\":\"0x47b760\",\"gasUsed\":\"0x21c687\",\"hash\":\"0xee5b9e9030d308c77a2d4f975b7090a026ac2cdfe9669e2452cedb4c82e8285e\",\"logsBloom\":\"\",\"miner\":\"0x004355941066a10e81641d660e0b7082176dfdc0\",\"mixHash\":\"0x0f15a659a35b19fc053b2d3202274ea010ff4125899e18f3197544ec7f53feda\",\"nonce\":\"0x12969144f99ecad6\",\"number\":\"0xc9e\",\"parentHash\":\"0xb4c2c0218039582ce398f2e6e6b325baa62348ee486abb259ea71a9b7afdd125\",\"receiptsRoot\":\"0xb52507949b00d49ae98e6918f80813df0ce6cc62e87bff83387dce37bee0ed7f\",\"sha3Uncles\":\"0x1dcc4de8dec75d7aab85b567b6ccd41ad312451b948a7413f0a142fd40d49347\",\"size\":\"0x21fe\",\"stateRoot\":\"0x3e3e38f228413700e8c88f87edc51bf696e10652909cd32f6ac87f425c885948\",\"timestamp\":\"0x5832272b\",\"totalDifficulty\":\"0xdf702046\",\"transactions\":[{\"blockHash\":\"0xee5b9e9030d308c77a2d4f975b7090a026ac2cdfe9669e2452cedb4c82e8285e\",\"blockNumber\":\"0xc9e\",\"from\":\"0x0718197b9ac69127381ed0c4b5d0f724f857c4d1\",\"gas\":\"0x3d0900\",\"gasPrice\":\"0x0\",\"hash\":\"0x0a00ca4ab70707c097381f5f83c08eac50c4c8a8f9bf025d407b0d71703c5971\",\"input\":\"\",\"nonce\":\"0x0\",\"to\":null,\"transactionIndex\":\"0x0\",\"value\":\"0x0\",\"v\":\"0x2a\",\"r\":\"0x5bf15422eec1ff6c602b1e1f4513aa577d737c105484744ed7722609357ae81d\",\"s\":\"0xcb1249d0abaeb172954896c495dab2f21099fb54a3b1a82adaacc1840bb24b7\"}],\"transactionsRoot\":\"0x42091b3f1a6dc05c829585b7957aec41b4228e993b6a9223ab3f4ba7277fbb5d\",\"uncles\":[]}}";

    // CHECKSTYLE:ON
    @Test
    public void testRawResponseEnabled() {
        configureWeb3Service(true);
        final Web3ClientVersion web3ClientVersion = deserialiseWeb3ClientVersionResponse();
        Assert.assertThat(web3ClientVersion.getRawResponse(), CoreMatchers.is(RawResponseTest.RAW_RESPONSE));
    }

    @Test
    public void testLargeRawResponseEnabled() {
        configureWeb3Service(true);
        buildResponse(RawResponseTest.LARGE_RAW_RESPONSE);
        EthBlock ethBlock = deserialiseResponse(EthBlock.class);
        Assert.assertThat(ethBlock.getRawResponse(), CoreMatchers.is(RawResponseTest.LARGE_RAW_RESPONSE));
    }

    @Test
    public void testRawResponseDisabled() {
        configureWeb3Service(false);
        final Web3ClientVersion web3ClientVersion = deserialiseWeb3ClientVersionResponse();
        Assert.assertThat(web3ClientVersion.getRawResponse(), CoreMatchers.nullValue());
    }
}

